import { NEW_POST } from "./types";

export const createPost = (postData) => {
  return function (dispatch) {
    fetch("https://jsonplaceholder.typicode.com/posts", {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(postData),
    })
      .then((res) => res.json())
      .then((post) =>
        dispatch({
          type: NEW_POST,
          payload: post,
        })
      );
  };
};
